import React, { useState, useEffect, useRef } from "react";
import "./home.css"; // Updated CSS file

const Certificate = () => {
  const [slideIndex, setSlideIndex] = useState(0);
  const slideWrapperRef = useRef(null); // Reference to the slide wrapper
  const slides = [
    { image: "/images/c1.jpg" },
    { image: "/images/c2.jpg" },
    { image: "/images/c3.jpg" },
    { image: "/images/c4.jpg" },
    { image: "/images/c5.jpg" },
    { image: "/images/c6.jpg" },
    { image: "/images/c7.jpg" },
    { image: "/images/c8.jpg" }
  ];

  const totalSlides = slides.length;
  const slidesPerView = 4;

  const nextSlide = () => {
    setSlideIndex((prevIndex) =>
      prevIndex + slidesPerView >= totalSlides ? 0 : prevIndex + slidesPerView
    );
  };

  const prevSlide = () => {
    setSlideIndex((prevIndex) =>
      prevIndex - slidesPerView < 0 ? totalSlides - slidesPerView : prevIndex - slidesPerView
    );
  };

  // Auto-scrolling using scrollLeft
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 3000); // Auto-slide every 3 seconds

    return () => clearInterval(interval);
  }, [slideIndex]);

  useEffect(() => {
    if (slideWrapperRef.current) {
      // Scroll to the current slide index
      slideWrapperRef.current.scrollTo({
        left: (slideIndex * (100 / slidesPerView)) + '%', // Adjust scroll position based on slide index
        behavior: 'smooth', // Smooth scrolling
      });
    }
  }, [slideIndex]);

  return (
    <section className="certificate_container">
      <div className="section-header">
        Performance
      </div>
      
      <div className="carousel-container">
        <button className="prev" onClick={prevSlide}>&#10094;</button>
        <div className="slide_wrapper" ref={slideWrapperRef}>
          {slides.map((slide, index) => (
            <div key={index} className="slide">
              <img src={slide.image} alt={`Slide ${index}`} />
            </div>
          ))}
        </div>
        <button className="next" onClick={nextSlide}>&#10095;</button>
      </div>
    </section>
  );
};

export default Certificate;
