import React, { useState, useEffect } from "react";
import "./home.css"; // Updated CSS file

const Certificate = () => {
  const [slideIndex, setSlideIndex] = useState(0);
  const slides = [
    { image: "/images/slider1.jpg" },
    { image: "/images/slider2.jpg" },
    { image: "/images/slider3.jpg" },
    { image: "/images/slider4.jpg" },
    { image: "/images/slider5.jpg" },
    { image: "/images/slider6.jpg" },
    { image: "/images/slider7.jpg" },
    { image: "/images/slider8.jpg" }
  ];

  const totalSlides = slides.length;
  const slidesPerView = 4;

  const nextSlide = () => {
    setSlideIndex((prevIndex) =>
      prevIndex + slidesPerView >= totalSlides ? 0 : prevIndex + slidesPerView
    );
  };

  const prevSlide = () => {
    setSlideIndex((prevIndex) =>
      prevIndex - slidesPerView < 0 ? totalSlides - slidesPerView : prevIndex - slidesPerView
    );
  };

  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 3000); // Auto-slide every 3 seconds

    return () => clearInterval(interval);
  }, [slideIndex]);

  return (
    
    <div className="certificate_container">
      <button className="prev" onClick={prevSlide}>&#10094;</button>
      <div className="slide_wrapper">
        {slides.slice(slideIndex, slideIndex + slidesPerView).map((slide, index) => (
          <div key={index} className="slide">
            <img src={slide.image} alt={`Slide ${index}`} />
          </div>
        ))}
      </div>
      <button className="next" onClick={nextSlide}>&#10095;</button>
    </div>
  );
};

export default Certificate;
